    \begin{tikzpicture}
        % Define los puntos A,B.
        \tkzDefPoint(1,1){A}
        \tkzDefPoint(5,1){B}

        % Define el punto C relativo a A.
        % C está a 4cm de A a 60 grados.
        \tkzDefShiftPoint[A](60:4){C}

