    \begin{tikzpicture}
        % Define la base AB.
        \tkzDefPoint(0,0){A}
        \tkzDefPoint(4,0){B}

        % Construye un triángulo isósceles rectángulo en C usando AB como base.
        \tkzDefTriangle[isosceles right](A,B)
            \tkzGetPoint{C}

        % Dibuja el triángulo.
        \tkzDrawPolygons(A,B,C)

        % Marca el ángulo recto en C.
