        \tkzMarkAngle[mark=||,size=0.5](C,B,A)

        % Dibuja y etiqueta los puntos.
        \tkzDrawPoints(A,B,C)
        \tkzLabelPoints(A,B)
        \tkzLabelPoints[above](C) % por encima.
    \end{tikzpicture}
\end{document}

