        % Paso 6: Marca y rellena el ángulo
        \tkzFillAngle[fill=red!30](B,A,C)
        \tkzMarkAngle(B,A,C)

        % Paso 7: Dibuja los segmentos de los rayos
        \tkzDrawLines[add=0 and 0.3, -Triangle](A,C A,B)

        % Paso 8: Agrega leyenda al gráfico
