        % cada uno de los vertices del hexágono
        \foreach \j in {1,...,6} { \tkzDrawSegment[dashed](O,Q\j)}

        % Paso 5: Coloca una leyenda debajo de la figura
        \tkzText(12.9,0){\large{b) Hexágono}}        
    \end{tikzpicture}
