\documentclass[tikz,border=2mm]{standalone}
\usepackage{xcolor}
\usepackage{tkz-base}
\usepackage{tkz-euclide}

\begin{document}
    \begin{tikzpicture}        
        % Paso 1: Define puntos de un radio.
        \tkzDefPoint(5,5){O} % Punto central
        \tkzDefPoint(8,5){A} % Punto de circunferencia

        % Paso 2: Dibuja la circunferencia
        \tkzDrawCircle(O,A)        

        % Paso 3: Define punto B en la circunferencia
        % a 40 grados del punto A
        \tkzDefPointOnCircle[through=center O angle 40 point A]
             \tkzGetPoint{B}

        % Paso 4: Define punto C en la circunferencia
        % a 80 grados del punto A
        \tkzDefPointOnCircle[through=center O angle 80 point A]
            \tkzGetPoint{C}

        % Paso 5: Define punto D en la circunferencia
        % a 190 grados del punto A
        \tkzDefPointOnCircle[through=center O angle 190 point A]
            \tkzGetPoint{D}

        % Paso 6: Define punto E en la circunferencia
        % a 220 grados del punto A            
        \tkzDefPointOnCircle[through=center O angle 220 point A]
            \tkzGetPoint{E}

        % Paso 7: Dibuja los puntos A,B,C,D,E en la circunferencia
        \tkzDrawPoints(O,A,B,C,D,E)

        % Paso 8: Rellena sector OB->C de color turquesa, opacidad 20%
        \tkzFillSector[teal!20](O,B)(C)

        % Paso 9: Rellena sector OD->E de un patrón de lineas azules
        \tkzFillSector[pattern=north east lines, pattern color=blue!40](O,D)(E)

        % Paso 10: Dibuja rayos OC y OB para resaltar sector circular
        \tkzDrawSegments[dashed](C,O O,B)

        % Paso 11: Dibuja rayos OD y OE para resaltar sector circular
        \tkzDrawSegments[dashed](D,O O,E)        
    \end{tikzpicture}
\end{document}

\documentclass[tikz,border=2mm]{standalone}
\usepackage{xcolor}
\usepackage{tkz-base}
\usepackage{tkz-euclide}

\begin{document}
    \begin{tikzpicture}
        % Paso 1: Define puntos de la base del triángulo PQR.
        \tkzDefPoint(9,3.5){P}
        \tkzDefPoint(14,3.5){Q}

        % Paso 2: Define triángulo equilátero y encuentra punto R
        \tkzDefTriangle[equilateral](P,Q) \tkzGetPoint{R}        

        % Paso 3: Define círculo cuya circunferencia pasa por PPR
        % y determina el centro S del círculo.
        \tkzDefCircle[circum](P,Q,R) \tkzGetPoint{S}

        % Paso 4: Rellena el sector circula SR->P con líneas azules
        \tkzFillSector[pattern=north east lines, pattern color=blue](S,R)(P)

        % Paso 5: Rellena el sector circula SQ->R con líneas color oliva
        \tkzFillSector[pattern=north west lines,pattern color=olive](S,Q)(R)

        % Paso 6: Rellena el sector circula SP->Q con líneas color rojo
        \tkzFillSector[pattern=horizontal lines, pattern color=red](S,P)(Q)

        % Paso 7: Rellena de blanco el polígono del triángulo PQR
        \tkzFillPolygon[white](P,Q,R)

        % Paso 8: Dibuja los puntos del triángulo PQR
        \tkzDrawPoints(P,Q,R)

        % Paso 9: Dibuja el polígulo triangular PQR
        \tkzDrawPolygon(P,Q,R)

        % Paso 10: Dibuja la circunferencia SP
        \tkzDrawCircle[thick](S,P)        
    \end{tikzpicture}
\end{document}

